mapper.select:
/* com.innobiz.orange.web.ap.dao.ApAgnApntDDao.countApAgnApntD */
SELECT COUNT(*) CNT
FROM AP_AGN_APNT_D T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.AGNT_UID = #{agntUid, jdbcType=VARCHAR}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	AND T.END_DT >= CONVERT(DATETIME, #{durEndDt, jdbcType=VARCHAR}, 120)
	AND T.STRT_DT <= CONVERT(DATETIME, #{durStrtDt, jdbcType=VARCHAR}, 120)
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApAgnApntDDao.insertApAgnApntD */ INSERT INTO AP_AGN_APNT_D
USER_UID, AGNT_UID, SEQ, END_DT, STRT_DT, AGNT_RESC_ID, ABS_RSON_CD, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{userUid, jdbcType=VARCHAR}, #{agntUid, jdbcType=VARCHAR}, #{seq, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SEQ),0)+1 FROM AP_AGN_APNT_D WHERE USER_UID = #{userUid, jdbcType=VARCHAR} AND AGNT_UID = #{agntUid, jdbcType=VARCHAR}), GETDATE(),NULL, CONVERT(DATETIME, #{endDt, jdbcType=VARCHAR}, 120), GETDATE(),NULL, CONVERT(DATETIME, #{strtDt, jdbcType=VARCHAR}, 120), #{agntRescId, jdbcType=VARCHAR}, #{absRsonCd, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, GETDATE(),GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApAgnApntDDao.deleteApAgnApntD */
DELETE FROM AP_AGN_APNT_D

	AND USER_UID = #{userUid, jdbcType=VARCHAR}
	AND AGNT_UID = #{agntUid, jdbcType=VARCHAR}
	AND SEQ = #{seq, jdbcType=INTEGER}
	AND END_DT = CONVERT(DATETIME, #{endDt, jdbcType=VARCHAR}, 120)
	AND T.END_DT >= CONVERT(DATETIME, #{durEndDt, jdbcType=VARCHAR}, 120)
	AND T.STRT_DT <= CONVERT(DATETIME, #{durStrtDt, jdbcType=VARCHAR}, 120)
	${whereSqllet}
