mapper.select:
/* com.innobiz.orange.web.ap.dao.ApFormBxDDao.countApFormBxD */
SELECT COUNT(*) CNT
FROM AP_FORM_BX_D T

	AND T.FORM_BX_ID = #{formBxId, jdbcType=VARCHAR}
	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.FORM_BX_PID = #{formBxPid, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	AND T.FORM_BX_ID IN (SELECT FORM_BX_ID FROM AP_FORM_BX_DEPT_R WHERE FORM_BX_DEPT_ID IN #{item, jdbcType=VARCHAR})
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApFormBxDDao.insertApFormBxD */ INSERT INTO AP_FORM_BX_D
FORM_BX_ID, COMP_ID, FORM_BX_NM, RESC_ID, FORM_BX_PID, SORT_ORDR, USE_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{formBxId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{formBxNm, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{formBxPid, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SORT_ORDR),0)+1 FROM AP_FORM_BX_D WHERE COMP_ID = #{compId, jdbcType=VARCHAR} AND FORM_BX_PID = #{formBxPid, jdbcType=VARCHAR}), #{useYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, GETDATE(),GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApFormBxDDao.updateApFormBxD */ UPDATE AP_FORM_BX_D
FORM_BX_ID = #{formBxId, jdbcType=VARCHAR}, COMP_ID = #{compId, jdbcType=VARCHAR}, FORM_BX_NM = #{formBxNm, jdbcType=NVARCHAR}, RESC_ID = #{rescId, jdbcType=VARCHAR}, FORM_BX_PID = #{formBxPid, jdbcType=VARCHAR}, SORT_ORDR = #{sortOrdr, jdbcType=INTEGER}, USE_YN = #{useYn, jdbcType=VARCHAR}, REGR_UID = #{regrUid, jdbcType=VARCHAR},
REG_DT = GETDATE(), REG_DT = NULL, REG_DT = CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),
MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = GETDATE(), MOD_DT = NULL, MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
AND FORM_BX_ID = #{formBxId, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApFormBxDDao.deleteApFormBxD */
DELETE FROM AP_FORM_BX_D

	AND FORM_BX_ID = #{formBxId, jdbcType=VARCHAR}
	${whereSqllet}
