mapper.select:
/* com.innobiz.orange.web.ap.dao.ApFormItemLDao.selectApFormItemL */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.FORM_ID DESC, N.FORM_COMB_SEQ DESC, N.ROW_NO DESC, N.COL_NO DESC) AS RNUM, N.* FROM (SELECT
	
	T.FORM_ID,
	T.FORM_COMB_SEQ,
	T.ROW_NO,
	T.COL_NO,
	T.CSPN_VA,
	T.ITEM_ID,

FROM AP_FORM_ITEM_L T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_COMB_SEQ = #{formCombSeq, jdbcType=INTEGER}
	AND T.ROW_NO = #{rowNo, jdbcType=INTEGER}
	AND T.COL_NO = #{colNo, jdbcType=INTEGER}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.FORM_ID, T.FORM_COMB_SEQ, T.ROW_NO, T.COL_NO


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApFormItemLDao.countApFormItemL */
SELECT COUNT(*) CNT
FROM AP_FORM_ITEM_L T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_COMB_SEQ = #{formCombSeq, jdbcType=INTEGER}
	AND T.ROW_NO = #{rowNo, jdbcType=INTEGER}
	AND T.COL_NO = #{colNo, jdbcType=INTEGER}
	${whereSqllet}

