mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngdPichDDao.selectApOngdPichD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.APV_NO DESC, N.PICH_TYP_CD DESC) AS RNUM, N.* FROM (SELECT
	
	T.APV_NO,
	T.PICH_TYP_CD,
	T.PICH_UID,
	T.PICH_DEPT_ID,
	T.PICH_NM,
	T.PICH_DEPT_NM,
	T.PICH_OPIN_CONT,
	T.HDL_STAT_CD,
	CONVERT(VARCHAR(19), T.HDL_DT, 120) HDL_DT,
	CONVERT(VARCHAR(19), T.VW_DT, 120) VW_DT,
	CONVERT(VARCHAR(19), T.PREV_APVR_APV_DT, 120) PREV_APVR_APV_DT,
	(SELECT RESC_VA FROM PT_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM PT_CD_B WHERE CLS_CD = 'HDL_STAT_CD' AND CD = T.HDL_STAT_CD) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) HDL_STAT_NM,

FROM AP_${storage}_PICH_D T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.PICH_TYP_CD = #{pichTypCd, jdbcType=VARCHAR}
	AND T.PICH_UID = #{pichUid, jdbcType=VARCHAR}
	AND T.PICH_DEPT_ID = #{pichDeptId, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.APV_NO, T.PICH_TYP_CD


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApOngdPichDDao.countApOngdPichD */
SELECT COUNT(*) CNT
FROM AP_${storage}_PICH_D T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.PICH_TYP_CD = #{pichTypCd, jdbcType=VARCHAR}
	AND T.PICH_UID = #{pichUid, jdbcType=VARCHAR}
	AND T.PICH_DEPT_ID = #{pichDeptId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApOngdPichDDao.insertApOngdPichD */ INSERT INTO AP_${storage}_PICH_D
APV_NO, PICH_TYP_CD, PICH_UID, PICH_DEPT_ID, PICH_NM, PICH_DEPT_NM, PICH_OPIN_CONT, HDL_STAT_CD, HDL_DT, VW_DT, PREV_APVR_APV_DT,
#{apvNo, jdbcType=BIGINT}, #{pichTypCd, jdbcType=VARCHAR}, #{pichUid, jdbcType=VARCHAR}, #{pichDeptId, jdbcType=VARCHAR}, #{pichNm, jdbcType=NVARCHAR}, #{pichDeptNm, jdbcType=NVARCHAR}, #{pichOpinCont, jdbcType=NVARCHAR}, #{hdlStatCd, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{hdlDt, jdbcType=VARCHAR}, 120), GETDATE(),NULL, CONVERT(DATETIME, #{vwDt, jdbcType=VARCHAR}, 120), GETDATE(),NULL, CONVERT(DATETIME, #{prevApvrApvDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApOngdPichDDao.updateApOngdPichD */ UPDATE AP_${storage}_PICH_D
APV_NO = #{apvNo, jdbcType=BIGINT}, PICH_TYP_CD = #{pichTypCd, jdbcType=VARCHAR}, PICH_UID = #{pichUid, jdbcType=VARCHAR}, PICH_DEPT_ID = #{pichDeptId, jdbcType=VARCHAR}, PICH_NM = #{pichNm, jdbcType=NVARCHAR}, PICH_DEPT_NM = #{pichDeptNm, jdbcType=NVARCHAR}, PICH_OPIN_CONT = #{pichOpinCont, jdbcType=NVARCHAR}, HDL_STAT_CD = #{hdlStatCd, jdbcType=VARCHAR},
HDL_DT = GETDATE(), HDL_DT = NULL, HDL_DT = CONVERT(DATETIME, #{hdlDt, jdbcType=VARCHAR}, 120),
VW_DT = GETDATE(), VW_DT = NULL, VW_DT = CONVERT(DATETIME, #{vwDt, jdbcType=VARCHAR}, 120),
PREV_APVR_APV_DT = GETDATE(), PREV_APVR_APV_DT = NULL, PREV_APVR_APV_DT = CONVERT(DATETIME, #{prevApvrApvDt, jdbcType=VARCHAR}, 120),
AND APV_NO = #{apvNo, jdbcType=BIGINT} AND PICH_TYP_CD = #{pichTypCd, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApOngdPichDDao.deleteApOngdPichD */
DELETE FROM AP_${storage}_PICH_D

	AND APV_NO = #{apvNo, jdbcType=BIGINT}
	AND PICH_TYP_CD = #{pichTypCd, jdbcType=VARCHAR}
	${whereSqllet}
