mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngdSendDDao.selectApOngdSendD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.APV_NO DESC, N.SEND_SEQ DESC) AS RNUM, N.* FROM (SELECT
	
	T.APV_NO,
	T.SEND_SEQ,
	T.RECV_DEPT_ID,
	T.RECV_DEPT_NM,
	T.REF_DEPT_NM,
	T.RECV_DEPT_TYP_CD,
	T.HDL_STAT_CD,
	T.HDLR_UID,
	T.HDLR_NM,
	T.HDLR_OPIN_CONT,
	CONVERT(VARCHAR(19), T.HDL_DT, 120) HDL_DT,
	CONVERT(VARCHAR(19), T.VW_DT, 120) VW_DT,
	CONVERT(VARCHAR(19), T.PREV_APVR_APV_DT, 120) PREV_APVR_APV_DT,
	(SELECT RESC_VA FROM PT_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM PT_CD_B WHERE CLS_CD = 'RECV_DEPT_TYP_CD' AND CD = T.RECV_DEPT_TYP_CD) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) RECV_DEPT_TYP_NM,
	(SELECT RESC_VA FROM PT_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM PT_CD_B WHERE CLS_CD = 'HDL_STAT_CD' AND CD = T.HDL_STAT_CD) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) HDL_STAT_NM,

FROM AP_${storage}_SEND_D T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.SEND_SEQ = #{sendSeq, jdbcType=INTEGER}
	AND T.RECV_DEPT_ID = #{recvDeptId, jdbcType=VARCHAR}
	AND T.HDL_STAT_CD = #{hdlStatCd, jdbcType=VARCHAR}
	AND T.APV_NO IN (SELECT APV_NO FROM AP_${storage}_B WHERE ORGN_APV_NO = #{orgnApvNo, jdbcType=BIGINT})
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.APV_NO, T.SEND_SEQ


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApOngdSendDDao.countApOngdSendD */
SELECT COUNT(*) CNT
FROM AP_${storage}_SEND_D T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.SEND_SEQ = #{sendSeq, jdbcType=INTEGER}
	AND T.RECV_DEPT_ID = #{recvDeptId, jdbcType=VARCHAR}
	AND T.HDL_STAT_CD = #{hdlStatCd, jdbcType=VARCHAR}
	AND T.APV_NO IN (SELECT APV_NO FROM AP_${storage}_B WHERE ORGN_APV_NO = #{orgnApvNo, jdbcType=BIGINT})
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApOngdSendDDao.insertApOngdSendD */ INSERT INTO AP_${storage}_SEND_D
APV_NO, SEND_SEQ, RECV_DEPT_ID, RECV_DEPT_NM, REF_DEPT_NM, RECV_DEPT_TYP_CD, HDL_STAT_CD, HDLR_UID, HDLR_NM, HDLR_OPIN_CONT, HDL_DT, VW_DT, PREV_APVR_APV_DT,
#{apvNo, jdbcType=BIGINT}, #{sendSeq, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SEND_SEQ),0)+1 FROM AP_${storage}_SEND_D WHERE APV_NO = #{apvNo, jdbcType=VARCHAR}), #{recvDeptId, jdbcType=VARCHAR}, #{recvDeptNm, jdbcType=NVARCHAR}, #{refDeptNm, jdbcType=NVARCHAR}, #{recvDeptTypCd, jdbcType=VARCHAR}, #{hdlStatCd, jdbcType=VARCHAR}, #{hdlrUid, jdbcType=VARCHAR}, #{hdlrNm, jdbcType=NVARCHAR}, #{hdlrOpinCont, jdbcType=NVARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{hdlDt, jdbcType=VARCHAR}, 120), GETDATE(),NULL, CONVERT(DATETIME, #{vwDt, jdbcType=VARCHAR}, 120), GETDATE(),NULL, CONVERT(DATETIME, #{prevApvrApvDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApOngdSendDDao.updateApOngdSendD */ UPDATE AP_${storage}_SEND_D
APV_NO = #{apvNo, jdbcType=BIGINT}, SEND_SEQ = #{sendSeq, jdbcType=INTEGER}, RECV_DEPT_ID = #{recvDeptId, jdbcType=VARCHAR}, RECV_DEPT_NM = #{recvDeptNm, jdbcType=NVARCHAR}, REF_DEPT_NM = #{refDeptNm, jdbcType=NVARCHAR}, RECV_DEPT_TYP_CD = #{recvDeptTypCd, jdbcType=VARCHAR}, HDL_STAT_CD = #{hdlStatCd, jdbcType=VARCHAR}, HDLR_UID = #{hdlrUid, jdbcType=VARCHAR}, HDLR_NM = #{hdlrNm, jdbcType=NVARCHAR}, HDLR_OPIN_CONT = #{hdlrOpinCont, jdbcType=NVARCHAR},
HDL_DT = GETDATE(), HDL_DT = NULL, HDL_DT = CONVERT(DATETIME, #{hdlDt, jdbcType=VARCHAR}, 120),
VW_DT = GETDATE(), VW_DT = NULL, VW_DT = CONVERT(DATETIME, #{vwDt, jdbcType=VARCHAR}, 120),
PREV_APVR_APV_DT = GETDATE(), PREV_APVR_APV_DT = NULL, PREV_APVR_APV_DT = CONVERT(DATETIME, #{prevApvrApvDt, jdbcType=VARCHAR}, 120),
AND APV_NO = #{apvNo, jdbcType=BIGINT} AND SEND_SEQ = #{sendSeq, jdbcType=INTEGER} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApOngdSendDDao.deleteApOngdSendD */
DELETE FROM AP_${storage}_SEND_D

	AND APV_NO = #{apvNo, jdbcType=BIGINT}
	AND SEND_SEQ = #{sendSeq, jdbcType=INTEGER}
	${whereSqllet}
