mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngoFormCombDDao.selectApOngoFormCombD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.FORM_ID DESC, N.FORM_SEQ DESC, N.FORM_COMB_ID DESC, N.FORM_COMB_SEQ DESC) AS RNUM, N.* FROM (SELECT
	
	T.FORM_ID,
	T.FORM_SEQ,
	T.FORM_COMB_ID,
	T.FORM_COMB_SEQ,
	T.SORT_ORDR,

FROM AP_ONGO_FORM_COMB_D T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_SEQ = #{formSeq, jdbcType=INTEGER}
	AND T.FORM_COMB_ID = #{formCombId, jdbcType=VARCHAR}
	AND T.FORM_COMB_SEQ = #{formCombSeq, jdbcType=INTEGER}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.FORM_ID, T.FORM_SEQ, T.FORM_COMB_ID, T.FORM_COMB_SEQ


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApOngoFormCombDDao.countApOngoFormCombD */
SELECT COUNT(*) CNT
FROM AP_ONGO_FORM_COMB_D T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_SEQ = #{formSeq, jdbcType=INTEGER}
	AND T.FORM_COMB_ID = #{formCombId, jdbcType=VARCHAR}
	AND T.FORM_COMB_SEQ = #{formCombSeq, jdbcType=INTEGER}
	${whereSqllet}

