/* com.innobiz.orange.web.ap.dao.ApOngoFormCombDDao.insertApOngoFormCombD */ INSERT INTO AP_ONGO_FORM_COMB_D
FORM_ID, FORM_SEQ, FORM_COMB_ID, FORM_COMB_SEQ, SORT_ORDR,
#{formId, jdbcType=VARCHAR}, #{formSeq, jdbcType=INTEGER}, #{formCombId, jdbcType=VARCHAR}, #{formCombSeq, jdbcType=INTEGER}, #{sortOrdr, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SORT_ORDR),0)+1 FROM AP_ONGO_FORM_COMB_D),

