	FORM_COMB_SEQ = #{formCombSeq, jdbcType=INTEGER},
	SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},


	AND FORM_ID = #{formId, jdbcType=VARCHAR}
	AND FORM_SEQ = #{formSeq, jdbcType=INTEGER}
	AND FORM_COMB_ID = #{formCombId, jdbcType=VARCHAR}
	AND FORM_COMB_SEQ = #{formCombSeq, jdbcType=INTEGER}
	${whereSqllet}

