mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngoFormImgDDao.selectApOngoFormImgD */

SELECT

	M.*,
	(SELECT RESC_VA FROM PT_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM PT_CD_B WHERE CLS_CD = 'FORM_IMG_TYP_CD' AND CD = M.FORM_IMG_TYP_CD) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) FORM_IMG_TYP_NM,

FROM ( SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.FORM_ID DESC, N.FORM_SEQ DESC, N.FORM_IMG_TYP_CD DESC) AS RNUM, N.* FROM (SELECT
	
	T.FORM_ID,
	T.FORM_SEQ,
	T.FORM_IMG_TYP_CD,
	T.IMG_PATH,
	T.IMG_WDTH,
	T.IMG_HGHT,
	
	(SELECT RESC_VA FROM PT_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM PT_CD_B WHERE CLS_CD = 'FORM_IMG_TYP_CD' AND CD = T.FORM_IMG_TYP_CD) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) FORM_IMG_TYP_NM,
	

FROM AP_ONGO_FORM_IMG_D T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_SEQ = #{formSeq, jdbcType=INTEGER}
	AND T.FORM_IMG_TYP_CD = #{formImgTypCd, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.FORM_ID, T.FORM_SEQ, T.FORM_IMG_TYP_CD


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.ap.dao.ApOngoFormImgDDao.countApOngoFormImgD */
SELECT COUNT(*) CNT
FROM AP_ONGO_FORM_IMG_D T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_SEQ = #{formSeq, jdbcType=INTEGER}
	AND T.FORM_IMG_TYP_CD = #{formImgTypCd, jdbcType=VARCHAR}
	${whereSqllet}

