mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngoFormXmlDDao.selectApOngoFormXmlD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.FORM_ID DESC, N.FORM_XML_CD DESC) AS RNUM, N.* FROM (SELECT
	
	T.FORM_ID,
	T.FORM_XML_CD,
	T.FORM_XML_CONT,

FROM AP_ONGO_FORM_XML_D T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_XML_CD = #{formXmlCd, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.FORM_ID, T.FORM_XML_CD


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApOngoFormXmlDDao.countApOngoFormXmlD */
SELECT COUNT(*) CNT
FROM AP_ONGO_FORM_XML_D T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_XML_CD = #{formXmlCd, jdbcType=VARCHAR}
	${whereSqllet}

