	AND FORM_ID = #{formId, jdbcType=VARCHAR}
	AND FORM_XML_CD = #{formXmlCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApOngoFormXmlDDao.deleteApOngoFormXmlD */
DELETE FROM AP_ONGO_FORM_XML_D

	AND FORM_ID = #{formId, jdbcType=VARCHAR}
