mapper.select:
/* com.innobiz.orange.web.ap.dao.ApPsnClsInfoDDao.selectApPsnClsInfoD */

SELECT

	M.*,
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = M.USER_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) USER_NM,

FROM ( SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.USER_UID DESC, N.PSN_CLS_INFO_ID DESC) AS RNUM, N.* FROM (SELECT
	
	T.USER_UID,
	T.PSN_CLS_INFO_ID,
	T.PSN_CLS_INFO_PID,
	T.PSN_CLS_INFO_NM,
	T.SORT_ORDR,
	T.USE_YN,
	
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.USER_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) USER_NM,
	

FROM AP_PSN_CLS_INFO_D T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.PSN_CLS_INFO_ID = #{psnClsInfoId, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.USER_UID, T.PSN_CLS_INFO_ID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.ap.dao.ApPsnClsInfoDDao.countApPsnClsInfoD */
SELECT COUNT(*) CNT
FROM AP_PSN_CLS_INFO_D T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.PSN_CLS_INFO_ID = #{psnClsInfoId, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApPsnClsInfoDDao.insertApPsnClsInfoD */ INSERT INTO AP_PSN_CLS_INFO_D
USER_UID, PSN_CLS_INFO_ID, PSN_CLS_INFO_PID, PSN_CLS_INFO_NM, SORT_ORDR, USE_YN,
#{userUid, jdbcType=VARCHAR}, #{psnClsInfoId, jdbcType=VARCHAR}, #{psnClsInfoPid, jdbcType=VARCHAR}, #{psnClsInfoNm, jdbcType=NVARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SORT_ORDR),0)+1 FROM AP_PSN_CLS_INFO_D WHERE USER_UID = #{userUid, jdbcType=VARCHAR} AND PSN_CLS_INFO_PID = #{psnClsInfoPid, jdbcType=VARCHAR}), #{useYn, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApPsnClsInfoDDao.updateApPsnClsInfoD */
UPDATE AP_PSN_CLS_INFO_D

	USER_UID = #{userUid, jdbcType=VARCHAR},
	PSN_CLS_INFO_ID = #{psnClsInfoId, jdbcType=VARCHAR},
	PSN_CLS_INFO_PID = #{psnClsInfoPid, jdbcType=VARCHAR},
	PSN_CLS_INFO_NM = #{psnClsInfoNm, jdbcType=NVARCHAR},
	SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},
	USE_YN = #{useYn, jdbcType=VARCHAR},


	AND USER_UID = #{userUid, jdbcType=VARCHAR}
	AND PSN_CLS_INFO_ID = #{psnClsInfoId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApPsnClsInfoDDao.deleteApPsnClsInfoD */
DELETE FROM AP_PSN_CLS_INFO_D

	AND USER_UID = #{userUid, jdbcType=VARCHAR}
	AND PSN_CLS_INFO_ID = #{psnClsInfoId, jdbcType=VARCHAR}
	${whereSqllet}
