mapper.select:
/* com.innobiz.orange.web.ap.dao.ApStorCompRDao.selectApStorCompR */

SELECT

	M.*,
	(SELECT RESC_VA FROM AP_RESC_B WHERE RESC_ID = M.STOR_RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) STOR_RESC_NM,

FROM ( SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.STOR_ID DESC, N.COMP_ID DESC) AS RNUM, N.* FROM (SELECT
	
	T.STOR_ID,
	T.COMP_ID,
	T.STOR_RESC_ID,
	T.COMP_DOC_CNT,
	T.USE_YN,
	
	(SELECT RESC_VA FROM AP_RESC_B WHERE RESC_ID = T.STOR_RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) STOR_RESC_NM,
	

FROM AP_STOR_COMP_R T

	AND T.STOR_ID = #{storId, jdbcType=VARCHAR}
	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.STOR_ID, T.COMP_ID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.ap.dao.ApStorCompRDao.countApStorCompR */
SELECT COUNT(*) CNT
FROM AP_STOR_COMP_R T

	AND T.STOR_ID = #{storId, jdbcType=VARCHAR}
	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApStorCompRDao.insertApStorCompR */ INSERT INTO AP_STOR_COMP_R
STOR_ID, COMP_ID, STOR_RESC_ID, COMP_DOC_CNT, USE_YN,
#{storId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{storRescId, jdbcType=VARCHAR}, NULL#{compDocCnt, jdbcType=BIGINT}, #{useYn, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApStorCompRDao.updateApStorCompR */
UPDATE AP_STOR_COMP_R

	STOR_ID = #{storId, jdbcType=VARCHAR},
	COMP_ID = #{compId, jdbcType=VARCHAR},
	STOR_RESC_ID = #{storRescId, jdbcType=VARCHAR},
	COMP_DOC_CNT = NULL#{compDocCnt, jdbcType=BIGINT},
	USE_YN = #{useYn, jdbcType=VARCHAR},


	AND STOR_ID = #{storId, jdbcType=VARCHAR}
	AND COMP_ID = #{compId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApStorCompRDao.deleteApStorCompR */
DELETE FROM AP_STOR_COMP_R

	AND STOR_ID = #{storId, jdbcType=VARCHAR}
	AND COMP_ID = #{compId, jdbcType=VARCHAR}
	${whereSqllet}
