/* com.innobiz.orange.web.bb.dao.BaBullSubmLDao.selectBaBullSubmL */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}T.BULL_ID DESC) AS RNUM,
T.BULL_ID, T.BRD_ID, (SELECT RESC_VA FROM BA_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM BA_BRD_B WHERE BRD_ID = T.BRD_ID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) BRD_NM, T.SUBJ, T.CONT, T.BULL_STAT_CD, T.BRD_ID_LIST, T.RJT_OPIN, T.DISCR_UID, T.DISC_DT, T.REGR_UID, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) REGR_NM, T.REG_DT, T.MODR_UID, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.MODR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) MODR_NM, T.MOD_DT, ISNULL(O.SECU_YN, 'N') SECU_YN, ISNULL(O.UGNT_YN, 'N') UGNT_YN, ISNULL(O.NOTC_YN, 'N') NOTC_YN, (SELECT RESC_VA FROM OR_RESC_B WHERE LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko') AND RESC_ID = (SELECT DEPT_RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID)) AS DEPT_NM,
FROM BA_BULL_SUBM_L T LEFT JOIN BA_BULL_OPT_D O ON T.BULL_ID = O.BULL_ID
LEFT JOIN BA_BRD_B B ON T.BRD_ID = B.BRD_ID LEFT JOIN BA_RESC_B R ON B.RESC_ID = R.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')
AND T.REGR_UID = #{regrUid, jdbcType=VARCHAR} AND T.DISCR_UID = #{discrUid, jdbcType=VARCHAR} AND T.BULL_STAT_CD = #{bullStatCd, jdbcType=VARCHAR} AND T.BULL_ID = #{bullId, jdbcType=INTEGER} AND T.BRD_ID = #{brdId, jdbcType=VARCHAR} AND T.SUBJ like '%' + #{schWord, jdbcType=VARCHAR} + '%' AND T.CONT like '%' + #{schWord, jdbcType=VARCHAR} + '%' AND R.RESC_VA like '%' + #{schWord, jdbcType=VARCHAR} + '%' ${whereSqllet}
ORDER BY ${orderBy} ORDER BY T.BULL_ID DESC
) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.bb.dao.BaBullSubmLDao.countBaBullSubmL */
SELECT COUNT(*) CNT
FROM BA_BULL_SUBM_L T

	LEFT JOIN BA_BRD_B B ON T.BRD_ID = B.BRD_ID
	LEFT JOIN BA_RESC_B R ON B.RESC_ID = R.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')


	 AND T.REGR_UID = #{regrUid, jdbcType=VARCHAR}
	 AND T.DISCR_UID = #{discrUid, jdbcType=VARCHAR}
	 AND T.BULL_STAT_CD = #{bullStatCd, jdbcType=VARCHAR}
	 AND T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND T.BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND T.SUBJ like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 AND T.CONT like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 AND R.RESC_VA like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 ${whereSqllet}

/* com.innobiz.orange.web.bb.dao.BaBullSubmLDao.selectNextId */
SELECT

	MAX(BULL_ID) AS BULL_ID,

FROM BA_BULL_SUBM_L T

	LEFT JOIN BA_BRD_B B ON T.BRD_ID = B.BRD_ID
	LEFT JOIN BA_RESC_B R ON B.RESC_ID = R.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')


	 AND T.REGR_UID = #{regrUid, jdbcType=VARCHAR}
	 AND T.DISCR_UID = #{discrUid, jdbcType=VARCHAR}
	 AND T.BULL_STAT_CD = #{bullStatCd, jdbcType=VARCHAR}
	AND T.BULL_ID < #{bullId, jdbcType=INTEGER}
	 AND T.BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND T.SUBJ like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 AND T.CONT like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 AND R.RESC_VA like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 ${whereSqllet}

/* com.innobiz.orange.web.bb.dao.BaBullSubmLDao.selectPrevId */
SELECT

	MIN(BULL_ID) AS BULL_ID,

FROM BA_BULL_SUBM_L T

	LEFT JOIN BA_BRD_B B ON T.BRD_ID = B.BRD_ID
	LEFT JOIN BA_RESC_B R ON B.RESC_ID = R.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')


	 AND T.REGR_UID = #{regrUid, jdbcType=VARCHAR}
	 AND T.DISCR_UID = #{discrUid, jdbcType=VARCHAR}
	 AND T.BULL_STAT_CD = #{bullStatCd, jdbcType=VARCHAR}
	AND T.BULL_ID > #{bullId, jdbcType=INTEGER}
	 AND T.BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND T.SUBJ like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 AND T.CONT like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 AND R.RESC_VA like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 ${whereSqllet}

