BULL_ID, BRD_ID, SUBJ, CONT, BULL_STAT_CD, BRD_ID_LIST, RJT_OPIN, DISCR_UID, DISC_DT, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{bullId, jdbcType=VARCHAR}, #{brdId, jdbcType=VARCHAR}, #{subj, jdbcType=NVARCHAR}, #{cont, jdbcType=CLOB}, #{bullStatCd, jdbcType=VARCHAR}, #{brdIdList, jdbcType=VARCHAR}, #{rjtOpin, jdbcType=VARCHAR}, #{discrUid, jdbcType=VARCHAR}, CONVERT(DATETIME, #{discDt, jdbcType=VARCHAR}, 120), #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, GETDATE(),GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
