mapper.select:
/* com.innobiz.orange.web.bb.dao.BaRezvSaveLDao.selectBaRezvSaveL */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}T.BULL_ID DESC) AS RNUM,
T.BULL_ID, T.BRD_ID, (SELECT RESC_VA FROM BA_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM BA_BRD_B WHERE BRD_ID = T.BRD_ID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) BRD_NM, T.SUBJ, T.BULL_REZV_DT, T.CONT, T.BRD_ID_LIST, T.REGR_UID, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) REGR_NM, T.REG_DT, T.MODR_UID, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.MODR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) MODR_NM, T.MOD_DT, --(SELECT COUNT(*) FROM BA_BULL_FILE_D WHERE REF_ID = CONVERT(VARCHAR, T.BULL_ID)) FILE_CNT, ISNULL(O.SECU_YN, 'N') SECU_YN, ISNULL(O.UGNT_YN, 'N') UGNT_YN, ISNULL(O.NOTC_YN, 'N') NOTC_YN,
FROM BA_REZV_SAVE_L T LEFT JOIN BA_BULL_OPT_D O ON T.BULL_ID = O.BULL_ID
LEFT JOIN BA_BRD_B B ON T.BRD_ID = B.BRD_ID LEFT JOIN BA_RESC_B R ON B.RESC_ID = R.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')
AND T.REGR_UID = #{regrUid, jdbcType=VARCHAR} AND T.BULL_ID = #{bullId, jdbcType=INTEGER} AND T.BRD_ID = #{brdId, jdbcType=VARCHAR} AND T.SUBJ like '%' + #{schWord, jdbcType=VARCHAR} + '%' AND T.CONT like '%' + #{schWord, jdbcType=VARCHAR} + '%' AND R.RESC_VA like '%' + #{schWord, jdbcType=VARCHAR} + '%' ${whereSqllet}
ORDER BY ${orderBy} ORDER BY T.BULL_ID DESC
) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.bb.dao.BaRezvSaveLDao.countBaRezvSaveL */
SELECT COUNT(*) CNT
FROM BA_REZV_SAVE_L T

	LEFT JOIN BA_BRD_B B ON T.BRD_ID = B.BRD_ID
	LEFT JOIN BA_RESC_B R ON B.RESC_ID = R.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')


	AND T.REGR_UID = #{regrUid, jdbcType=VARCHAR}
	 AND T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND T.BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND T.SUBJ like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 AND T.CONT like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 AND R.RESC_VA like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 ${whereSqllet}

/* com.innobiz.orange.web.bb.dao.BaRezvSaveLDao.selectNextId */
SELECT

	MAX(BULL_ID) AS BULL_ID,

FROM BA_REZV_SAVE_L T

	LEFT JOIN BA_BRD_B B ON T.BRD_ID = B.BRD_ID
	LEFT JOIN BA_RESC_B R ON B.RESC_ID = R.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')


	AND T.REGR_UID = #{regrUid, jdbcType=VARCHAR}
	AND T.BULL_ID < #{bullId, jdbcType=INTEGER}
	 AND T.BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND T.SUBJ like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 AND T.CONT like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 AND R.RESC_VA like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 ${whereSqllet}

/* com.innobiz.orange.web.bb.dao.BaRezvSaveLDao.selectPrevId */
SELECT

	MIN(BULL_ID) AS BULL_ID,

FROM BA_REZV_SAVE_L T

	LEFT JOIN BA_BRD_B B ON T.BRD_ID = B.BRD_ID
	LEFT JOIN BA_RESC_B R ON B.RESC_ID = R.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')


	AND T.REGR_UID = #{regrUid, jdbcType=VARCHAR}
	AND T.BULL_ID > #{bullId, jdbcType=INTEGER}
	 AND T.BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND T.SUBJ like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 AND T.CONT like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 AND R.RESC_VA like '%' + #{schWord, jdbcType=VARCHAR} + '%'
	 ${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.bb.dao.BaRezvSaveLDao.insertBaRezvSaveL */ INSERT INTO BA_REZV_SAVE_L
BULL_ID, BRD_ID, SUBJ, BULL_REZV_DT, CONT, BRD_ID_LIST, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{bullId, jdbcType=VARCHAR}, #{brdId, jdbcType=VARCHAR}, #{subj, jdbcType=NVARCHAR}, NULL, CONVERT(DATETIME, #{bullRezvDt, jdbcType=VARCHAR}, 120), #{cont, jdbcType=CLOB}, #{brdIdList, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, GETDATE(),GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.bb.dao.BaRezvSaveLDao.updateBaRezvSaveL */ UPDATE BA_REZV_SAVE_L
BRD_ID = #{brdId, jdbcType=VARCHAR}, SUBJ = #{subj, jdbcType=NVARCHAR}, BULL_REZV_DT = CONVERT(DATETIME, #{bullRezvDt, jdbcType=VARCHAR}, 120), CONT = #{cont, jdbcType=CLOB}, BRD_ID_LIST = #{brdIdList, jdbcType=VARCHAR}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = GETDATE(), MOD_DT = NULL, MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
AND REGR_UID = #{regrUid, jdbcType=VARCHAR} AND BULL_ID = #{bullId, jdbcType=INTEGER} AND 1=0 ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.bb.dao.BaRezvSaveLDao.deleteBaRezvSaveL */
DELETE FROM BA_REZV_SAVE_L

	AND REGR_UID = #{regrUid, jdbcType=VARCHAR}
	 AND BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND 1=0
