mapper.select:
/* com.innobiz.orange.web.bb.dao.BaScreHstLDao.selectBaScreHstL */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}T.REG_DT DESC) AS RNUM,
	
	T.BULL_ID,
	T.USER_UID,
	T.SCRE,
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.USER_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) USER_NM,
	T.REG_DT,

FROM BA_SCRE_HST_L T

	 AND T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	 ${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.REG_DT DESC

) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.bb.dao.BaScreHstLDao.countBaScreHstL */
SELECT COUNT(*) CNT
FROM BA_SCRE_HST_L T

	 AND T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	 ${whereSqllet}

/* com.innobiz.orange.web.bb.dao.BaScreHstLDao.selectAvgScre */
SELECT AVG(SCRE)
FROM BA_SCRE_HST_L T

	 AND T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 ${whereSqllet}

