BULL_ID, USER_UID, SCRE, REG_DT,
#{bullId, jdbcType=VARCHAR}, #{userUid, jdbcType=VARCHAR}, #{scre, jdbcType=INTEGER}, GETDATE(),

mapper.update:
/* com.innobiz.orange.web.bb.dao.BaScreHstLDao.updateBaScreHstL */
UPDATE BA_SCRE_HST_L

	REG_DT = GETDATE(),


	 AND BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND USER_UID = #{userUid, jdbcType=VARCHAR}
	 AND 1=0
	 ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.bb.dao.BaScreHstLDao.deleteBaScreHstL */
