mapper.select:
/* com.innobiz.orange.web.ct.dao.CtDebrOpinDDao.selectCtDebrOpinD */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.OPIN_ORDR DESC) AS RNUM, N.* FROM ( SELECT
CDOD_T.COMP_ID, CDOD_T.CT_ID, CDOD_T.DEBR_ID, CDOD_T.OPIN_ORDR, CDOD_T.SUBJ, CDOD_T.OPIN, CDOD_T.READ_CNT, CDOD_T.PROS_CONS_CD, CDOD_T.REG_DT, CDOD_T.MOD_DT, CDOD_T.REGR_UID, CDOD_T.MODR_UID, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = CDOD_T.REGR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) REGR_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = CDOD_T.MODR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) MODR_NM,
FROM CT_DEBR_OPIN_D CDOD_T
LEFT JOIN OR_USER_B U ON CDOD_T.REGR_UID = U.USER_UID LEFT JOIN OR_RESC_B R ON U.RESC_ID = R.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')
ORDER BY ${orderBy} ORDER BY CDOD_T.OPIN_ORDR
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtDebrOpinDDao.insertCtDebrOpinD */ INSERT INTO CT_DEBR_OPIN_D
COMP_ID, CT_ID, DEBR_ID, OPIN_ORDR, SUBJ, OPIN, READ_CNT, PROS_CONS_CD, REG_DT, MOD_DT, REGR_UID, MODR_UID,
#{compId, jdbcType=VARCHAR}, #{ctId, jdbcType=VARCHAR}, #{debrId, jdbcType=VARCHAR}, #{opinOrdr, jdbcType=NUMERIC}, #{subj, jdbcType=NVARCHAR}, #{opin, jdbcType=CLOB}, #{readCnt, jdbcType=NUMERIC}, #{prosConsCd, jdbcType=CHAR}, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120), #{regrUid, jdbcType=VARCHAR}, #{modrUid, jdbcType=VARCHAR},

mapper.update:
		/* com.innobiz.orange.web.ct.dao.CtDebrOpinDDao.updateCtDebrOpinD */
		UPDATE CT_DEBR_OPIN_D
		
			 SUBJ = #{subj, jdbcType=NVARCHAR},
			 OPIN = #{opin, jdbcType=CLOB},
			 READ_CNT = #{readCnt, jdbcType=NUMERIC},
			 PROS_CONS_CD = #{prosConsCd, jdbcType=CHAR},
			 MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
			 MODR_UID = #{modrUid, jdbcType=VARCHAR},
			
		
		
			 AND COMP_ID = #{compId, jdbcType=VARCHAR}
   		     AND CT_ID = #{ctId, jdbcType=VARCHAR}
   		     AND DEBR_ID = #{debrId, jdbcType=VARCHAR}
   		     AND OPIN_ORDR = #{opinOrdr, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtDebrOpinDDao.updateReadCnt */
UPDATE CT_DEBR_OPIN_D

	READ_CNT = READ_CNT + 1,


	AND OPIN_ORDR = #{opinOrdr, jdbcType=NUMERIC}

mapper.delete:
		/* com.innobiz.orange.web.ct.dao.CtDebrOpinDDao.deleteCtDebrOpinD */
		DELETE FROM CT_DEBR_OPIN_D
		
			 AND COMP_ID = #{compId, jdbcType=VARCHAR}
   		     AND CT_ID = #{ctId, jdbcType=VARCHAR}
   		     AND DEBR_ID = #{debrId, jdbcType=VARCHAR}
   		     AND OPIN_ORDR = #{opinOrdr, jdbcType=NUMERIC}
