mapper.select:
/* com.innobiz.orange.web.ct.dao.CtPltSetupDDao.selectCtPltSetupD */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.CT_FNC_ID DESC) AS RNUM, N.* FROM ( SELECT
CFMAD_T.COMP_ID, CFMAD_T.CT_FNC_ID, CFMAD_T.USER_UID, CFMAD_T.CT_ID, CFMAD_T.PERD,
FROM CT_PLT_SETUP_D CFMAD_T
ORDER BY ${orderBy} ORDER BY CFMAD_T.CT_FNC_ID
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtPltSetupDDao.countCtPltSetupD */
SELECT COUNT(*) CNT
FROM CT_PLT_SETUP_D CFMAD_T

