mapper.select:
/* com.innobiz.orange.web.ct.dao.CtPolcDDao.selectCtPolcD */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.COMP_ID DESC) AS RNUM, N.* FROM ( SELECT
CPD_T.COMP_ID,
CPD_T.APVD_YN, CPD_T.REG_DT, CPD_T.MOD_DT, CPD_T.REGR_UID, CPD_T.MODR_UID,
FROM CT_POLC_D CPD_T
ORDER BY ${orderBy} ORDER BY CPD_T.COMP_ID
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtPolcDDao.countCtPolcD */
SELECT COUNT(*) CNT
FROM CT_POLC_D CPD_T

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtPolcDDao.insertCtPolcD */ INSERT INTO CT_POLC_D
COMP_ID, APVD_YN, REG_DT, MOD_DT, REGR_UID, MODR_UID,
#{compId, jdbcType=CHAR}, #{apvdYn, jdbcType=CHAR}, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120), #{regrUid, jdbcType=VARCHAR}, #{modrUid, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.ct.dao.CtPolcDDao.updateCtPolcD */
UPDATE CT_POLC_D

	 APVD_YN = #{apvdYn, jdbcType=CHAR},
	 MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
	 MODR_UID = #{modrUid, jdbcType=VARCHAR},


     AND COMP_ID = #{compId, jdbcType=CHAR}

mapper.delete:
/* com.innobiz.orange.web.ct.dao.CtPolcDDao.updateCtPolcD */
DELETE FROM CT_POLC_D

     AND COMP_ID = #{compId, jdbcType=CHAR}
