mapper.select:
/* com.innobiz.orange.web.ct.dao.CtSiteCatDDao.selectCtSiteCatD */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.CAT_ID DESC) AS RNUM, N.* FROM ( SELECT
CSCD_T.COMP_ID, CSCD_T.CT_ID, CSCD_T.CAT_ID, CSCD_T.CAT_NM_RESC_ID, CSCD_T.REG_DT, CSCD_T.MOD_DT, CSCD_T.REGR_UID, CSCD_T.MODR_UID, (SELECT RESC_VA FROM CT_RESC_B WHERE RESC_ID = CSCD_T.CAT_NM_RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) CAT_NM,
FROM CT_SITE_CAT_D CSCD_T
ORDER BY ${orderBy} ORDER BY CSCD_T.CAT_ID
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtSiteCatDDao.countCtSiteCatD */
SELECT COUNT(*) CNT
FROM CT_SITE_CAT_D CSCD_T

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtSiteCatDDao.insertCtSiteCatD */ INSERT INTO CT_SITE_CAT_D
COMP_ID, CT_ID, CAT_ID, CAT_NM_RESC_ID, REG_DT, MOD_DT, REGR_UID, MODR_UID,
#{compId, jdbcType=VARCHAR}, #{ctId, jdbcType=VARCHAR}, #{catId, jdbcType=VARCHAR}, #{catNmRescId, jdbcType=VARCHAR}, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120), #{regrUid, jdbcType=VARCHAR}, #{modrUid, jdbcType=VARCHAR},

mapper.update:
	/* com.innobiz.orange.web.ct.dao.CtSiteCatDDao.updateCtSiteCatD */
	UPDATE CT_SITE_CAT_D
	
		 CAT_NM_RESC_ID = #{catNmRescId, jdbcType=VARCHAR},
		 MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
		 MODR_UID = #{modrUid, jdbcType=VARCHAR},
	
	
	     AND COMP_ID = #{compId, jdbcType=VARCHAR}
		 AND CT_ID = #{ctId, jdbcType=VARCHAR}
	     AND CAT_ID = #{catId, jdbcType=VARCHAR}

mapper.delete:
	/* com.innobiz.orange.web.ct.dao.CtSiteCatDDao.deleteCtSiteCatD */
	DELETE FROM CT_SITE_CAT_D
	
	     AND COMP_ID = #{compId, jdbcType=VARCHAR}
		 AND CT_ID = #{ctId, jdbcType=VARCHAR}
	     AND CAT_ID = #{catId, jdbcType=VARCHAR}
