mapper.select:
/* com.innobiz.orange.web.dm.dao.DmCatDispDDao.selectDmCatDispD */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}T.ITEM_ID ASC) AS RNUM,
T.STOR_ID, T.CAT_ID, T.ITEM_ID, T.USE_YN, T.ATRB_ID, T.WDTH_PERC, T.ALN_VA, T.SORT_OPT_VA, T.DATA_SORT_VA, T.REG_DISP_YN, T.MOD_DISP_YN, T.READ_DISP_YN, T.LIST_DISP_YN, T.LIST_DISP_ORDR, T.ADD_ITEM_YN, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM DM_CAT_DISP_D T
ORDER BY ${orderBy} ORDER BY T.ITEM_ID ASC
) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.dm.dao.DmCatDispDDao.countDmCatDispD */
SELECT COUNT(*) CNT
FROM DM_CAT_DISP_D T

