mapper.select:
/* com.innobiz.orange.web.dm.dao.DmDocNoDDao.selectDmDocNoD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}T.DOC_SEQ ASC) AS RNUM,
	
	T.STOR_ID,			
	T.YY,
	T.ORG_ID,
	T.DOC_SEQ,

FROM DM_DOC_NO_D T

ORDER BY ${orderBy}
ORDER BY T.DOC_SEQ ASC

) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.dm.dao.DmDocNoDDao.countDmDocNoD */
SELECT COUNT(*) CNT
FROM DM_DOC_NO_D T

