STOR_ID, YY, ORG_ID, DOC_SEQ,
#{storId, jdbcType=VARCHAR}, #{yy, jdbcType=INTEGER}, #{orgId, jdbcType=VARCHAR}, #{docSeq, jdbcType=INTEGER},

