mapper.select:
/* com.innobiz.orange.web.dm.dao.DmFldBDao.selectDmFldB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.FLD_ID DESC) AS RNUM, N.* FROM (SELECT
T.STOR_ID, T.FLD_ID, T.FLD_GRP_ID, T.CAT_ID, (SELECT RESC_VA FROM DM_${prefix}RESC_B WHERE STOR_ID = T.STOR_ID AND RESC_ID = T.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) AS FLD_NM, T.RESC_ID, T.FLD_PID, T.FLD_TYP_CD, T.SORT_ORDR, T.USE_YN, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM DM_${prefix}FLD_B T
ORDER BY ${orderBy} ORDER BY T.REG_DT
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.dm.dao.DmFldBDao.countDmFldB */
SELECT COUNT(*) CNT
FROM DM_FLD_B T

mapper.insert:
/* com.innobiz.orange.web.dm.dao.DmFldBDao.insertDmFldB */ INSERT INTO DM_${prefix}FLD_B
STOR_ID, FLD_ID, FLD_GRP_ID, CAT_ID, FLD_NM, RESC_ID, FLD_PID, FLD_TYP_CD, SORT_ORDR, USE_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{storId, jdbcType=VARCHAR}, #{fldId, jdbcType=VARCHAR}, #{fldGrpId, jdbcType=VARCHAR}, #{catId, jdbcType=VARCHAR}, #{fldNm, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{fldPid, jdbcType=VARCHAR}, #{fldTypCd, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, #{useYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.dm.dao.DmFldBDao.updateDmFldB */ UPDATE DM_${prefix}FLD_B
FLD_NM = #{fldNm, jdbcType=VARCHAR}, CAT_ID = #{catId, jdbcType=VARCHAR},
FLD_TYP_CD = #{fldTypCd, jdbcType=VARCHAR}, FLD_GRP_ID = #{fldGrpId, jdbcType=VARCHAR},
FLD_PID = #{fldPid, jdbcType=VARCHAR}, SORT_ORDR = #{sortOrdr, jdbcType=INTEGER}, USE_YN = #{useYn, jdbcType=VARCHAR},
STOR_ID = #{storId, jdbcType=VARCHAR},
MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = GETDATE(), MOD_DT = NULL, MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
AND FLD_ID = #{fldId, jdbcType=VARCHAR}
AND STOR_ID = #{storId, jdbcType=VARCHAR}
AND REGR_UID = #{regrUid, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.dm.dao.DmFldBDao.deleteDmFldB */
DELETE FROM DM_${prefix}FLD_B

	 AND FLD_ID = #{fldId, jdbcType=VARCHAR}
	
		 AND STOR_ID = #{storId, jdbcType=VARCHAR}
	
	
		 AND REGR_UID = #{regrUid, jdbcType=VARCHAR}
