mapper.select:
/* com.innobiz.orange.web.dm.dao.DmKwdLDao.selectDmKwdL */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.KWD_NM DESC) AS RNUM, N.* FROM (SELECT
	
	T.DOC_GRP_ID,
	T.KWD_NM,

FROM DM_${tableName}_KWD_L T


	ORDER BY ${orderBy}
	ORDER BY T.KWD_NM


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.dm.dao.DmKwdLDao.countDmKwdL */
SELECT COUNT(*) CNT
FROM DM_${tableName}_KWD_L T

mapper.insert:
/* com.innobiz.orange.web.dm.dao.DmKwdLDao.insertDmKwdL */ INSERT INTO DM_${tableName}_KWD_L
DOC_GRP_ID, KWD_NM,
#{docGrpId, jdbcType=VARCHAR}, #{kwdNm, jdbcType=VARCHAR},

mapper.delete:
/* com.innobiz.orange.web.dm.dao.DmKwdLDao.deleteDmKwdL */
DELETE FROM DM_${tableName}_KWD_L

	 AND DOC_GRP_ID = #{docGrpId, jdbcType=VARCHAR}
