mapper.select:
/* com.innobiz.orange.web.dm.dao.DmStorDocRDao.selectDmStorDocR */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.STOR_ID DESC, N.DOC_GRP_ID DESC) AS RNUM, N.* FROM (SELECT
	
	T.DOC_GRP_ID,
	T.STOR_ID,			

FROM DM_STOR_DOC_R T

	AND T.STOR_ID = #{storId, jdbcType=BIGINT}
	AND T.DOC_GRP_ID = #{docGrpId, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.STOR_ID, T.DOC_GRP_ID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.dm.dao.DmStorDocRDao.countDmStorDocR */
SELECT COUNT(*) CNT
FROM DM_STOR_DOC_R T

	AND T.STOR_ID = #{storId, jdbcType=BIGINT}
	AND T.DOC_GRP_ID = #{docGrpId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.dm.dao.DmStorDocRDao.insertDmStorDocR */ INSERT INTO DM_STOR_DOC_R
DOC_GRP_ID, STOR_ID,
#{docGrpId, jdbcType=BIGINT}, #{storId, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.dm.dao.DmStorDocRDao.updateDmStorDocR */
UPDATE DM_STOR_DOC_R

	STOR_ID = #{storId, jdbcType=BIGINT},


	AND DOC_GRP_ID = #{docGrpId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.dm.dao.DmStorDocRDao.deleteDmStorDocR */
DELETE FROM DM_STOR_DOC_R

	AND STOR_ID = #{storId, jdbcType=BIGINT}
	AND DOC_GRP_ID = #{docGrpId, jdbcType=VARCHAR}
	${whereSqllet}
