mapper.select:
/* com.innobiz.orange.web.dm.dao.DmSubmLDao.selectDmSubmL */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.REG_DT DESC) AS RNUM, N.* FROM (SELECT
T.COMP_ID, T.DOC_GRP_ID, T.STAT_CD, T.RJT_OPIN, T.DISCR_UID, T.DISC_DT, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) REGR_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.DISCR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) DISCR_NM,
FROM DM_SUBM_L T
ORDER BY ${orderBy} ORDER BY T.REG_DT DESC
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.dm.dao.DmSubmLDao.countDmSubmL */
SELECT COUNT(*) CNT
FROM DM_SUBM_L T

