COMP_ID, DOC_GRP_ID, STAT_CD, RJT_OPIN, DISCR_UID, DISC_DT, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{compId, jdbcType=VARCHAR}, #{docGrpId, jdbcType=VARCHAR}, #{statCd, jdbcType=CHAR}, #{rjtOpin, jdbcType=VARCHAR}, #{discrUid, jdbcType=NVARCHAR}, #{discDt, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR},NULL, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.delete:
/* com.innobiz.orange.web.dm.dao.DmSubmLDao.deleteDmSubmL */
DELETE FROM DM_SUBM_L

	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
	 AND DOC_GRP_ID = #{docGrpId, jdbcType=VARCHAR}
	 AND REGR_UID = #{regrUid, jdbcType=VARCHAR}
