mapper.select:
/* com.innobiz.orange.web.em.dao.EmMailAdrTranBDao.selectEmMailAdrTranB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.TRAN_ID DESC) AS RNUM, N.* FROM (SELECT
T.TRAN_ID, T.USER_CNT, T.FLD_CNT, T.ADR_CNT, T.TRAN_STRT_DT, T.TRAN_END_DT, T.TRAN_PROC_STAT_CD, T.TRAN_CONT, T.ERR_CONT, T.USER_UID,
FROM EM_MAIL_ADR_TRAN_B T
ORDER BY ${orderBy} ORDER BY T.TRAN_ID DESC
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.em.dao.EmMailAdrTranBDao.countEmMailAdrTranB */
SELECT COUNT(*) CNT
FROM EM_MAIL_ADR_TRAN_B T

mapper.insert:
/* com.innobiz.orange.web.em.dao.EmMailAdrTranBDao.insertEmMailAdrTranB */ INSERT INTO EM_MAIL_ADR_TRAN_B
TRAN_ID, USER_CNT, FLD_CNT, ADR_CNT, TRAN_STRT_DT, TRAN_END_DT, TRAN_PROC_STAT_CD, TRAN_CONT, ERR_CONT, USER_UID,
#{tranId, jdbcType=VARCHAR}, #{userCnt, jdbcType=INTEGER}, #{fldCnt, jdbcType=INTEGER}, #{adrCnt, jdbcType=INTEGER}, GETDATE(),NULL, CONVERT(DATETIME, #{tranStrtDt, jdbcType=VARCHAR}, 120), GETDATE(),NULL, CONVERT(DATETIME, #{tranEndDt, jdbcType=VARCHAR}, 120), #{tranProcStatCd, jdbcType=VARCHAR}, #{tranCont, jdbcType=VARCHAR}, #{errCont, jdbcType=VARCHAR}, #{userUid, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.em.dao.EmMailAdrTranBDao.updateEmMailAdrTranB */ UPDATE EM_MAIL_ADR_TRAN_B
USER_CNT = #{userCnt, jdbcType=INTEGER}, FLD_CNT = #{fldCnt, jdbcType=INTEGER}, ADR_CNT = #{adrCnt, jdbcType=INTEGER},
TRAN_END_DT = GETDATE(), TRAN_END_DT = NULL, TRAN_END_DT = CONVERT(DATETIME, #{tranEndDt, jdbcType=VARCHAR}, 120),
TRAN_PROC_STAT_CD = #{tranProcStatCd, jdbcType=VARCHAR}, TRAN_CONT = #{tranCont, jdbcType=VARCHAR}, ERR_CONT = #{errCont, jdbcType=VARCHAR}, USER_UID = #{userUid, jdbcType=VARCHAR},
AND TRAN_ID = #{tranId, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.em.dao.EmMailAdrTranBDao.deleteEmMailAdrTranB */
DELETE FROM EM_MAIL_ADR_TRAN_B

	AND TRAN_ID = #{tranId, jdbcType=VARCHAR}
	${whereSqllet}
