mapper.select:
/* com.innobiz.orange.web.pt.dao.PtAuthGrpUserRDao.selectPtAuthGrpUserR */

SELECT

	M.*,
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = M.USER_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) USER_NM,

FROM ( SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.COMP_ID DESC, N.AUTH_GRP_ID DESC, N.EXCLI_YN DESC, N.USER_UID DESC) AS RNUM, N.* FROM (SELECT
	
	T.COMP_ID,
	T.AUTH_GRP_ID,
	T.EXCLI_YN,
	T.USER_UID,
	T.AUTH_GRP_TYP_CD,
	T.SORT_ORDR,
	
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.USER_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) USER_NM,
	

FROM PT_AUTH_GRP_USER_R T

	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.AUTH_GRP_ID = #{authGrpId, jdbcType=VARCHAR}
	AND T.EXCLI_YN = #{excliYn, jdbcType=VARCHAR}
	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.AUTH_GRP_TYP_CD = #{authGrpTypCd, jdbcType=VARCHAR}
	AND T.COMP_ID IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.COMP_ID, T.AUTH_GRP_ID, T.EXCLI_YN, T.USER_UID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.pt.dao.PtAuthGrpUserRDao.countPtAuthGrpUserR */
SELECT COUNT(*) CNT
FROM PT_AUTH_GRP_USER_R T

	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.AUTH_GRP_ID = #{authGrpId, jdbcType=VARCHAR}
	AND T.EXCLI_YN = #{excliYn, jdbcType=VARCHAR}
	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.AUTH_GRP_TYP_CD = #{authGrpTypCd, jdbcType=VARCHAR}
	AND T.COMP_ID IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}

