COMP_ID, AUTH_GRP_ID, EXCLI_YN, USER_UID, AUTH_GRP_TYP_CD, SORT_ORDR,
#{compId, jdbcType=VARCHAR}, #{authGrpId, jdbcType=VARCHAR}, #{excliYn, jdbcType=VARCHAR}, #{userUid, jdbcType=VARCHAR}, #{authGrpTypCd, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SORT_ORDR),0)+1 FROM PT_AUTH_GRP_USER_R WHERE COMP_ID = #{compId, jdbcType=VARCHAR} AND AUTH_GRP_ID = #{authGrpId, jdbcType=VARCHAR} AND EXCLI_YN = #{excliYn, jdbcType=VARCHAR}),

