mapper.select:
/* com.innobiz.orange.web.pt.dao.PtCacheTDao.selectPtCacheT */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.CACHE_CAT_ID DESC, N.LANG_TYP_CD DESC, N.CACHE_ID DESC) AS RNUM, N.* FROM (SELECT
	
	T.CACHE_CAT_ID,
	T.LANG_TYP_CD,
	T.CACHE_ID,
	CONVERT(VARCHAR(19), T.MOD_DT, 120) MOD_DT,

FROM PT_CACHE_T T

	AND T.CACHE_CAT_ID = #{cacheCatId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	AND T.CACHE_ID = #{cacheId, jdbcType=VARCHAR}
	AND MOD_DT > CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120)
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.CACHE_CAT_ID, T.LANG_TYP_CD, T.CACHE_ID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.pt.dao.PtCacheTDao.countPtCacheT */
SELECT COUNT(*) CNT
FROM PT_CACHE_T T

	AND T.CACHE_CAT_ID = #{cacheCatId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	AND T.CACHE_ID = #{cacheId, jdbcType=VARCHAR}
	AND MOD_DT > CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120)
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtCacheTDao.insertPtCacheT */ INSERT INTO PT_CACHE_T
CACHE_CAT_ID, LANG_TYP_CD, CACHE_ID, MOD_DT,
#{cacheCatId, jdbcType=VARCHAR}, #{langTypCd, jdbcType=VARCHAR}, #{cacheId, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.pt.dao.PtCacheTDao.updatePtCacheT */
UPDATE PT_CACHE_T

	CACHE_CAT_ID = #{cacheCatId, jdbcType=VARCHAR},
	LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR},
	CACHE_ID = #{cacheId, jdbcType=VARCHAR},
	
		MOD_DT = GETDATE(),
		MOD_DT = NULL,
		MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
	


	AND CACHE_CAT_ID = #{cacheCatId, jdbcType=VARCHAR}
	AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	AND CACHE_ID = #{cacheId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtCacheTDao.deletePtCacheT */
DELETE FROM PT_CACHE_T

	AND CACHE_CAT_ID = #{cacheCatId, jdbcType=VARCHAR}
	AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	AND CACHE_ID = #{cacheId, jdbcType=VARCHAR}
	${whereSqllet}
