mapper.select:
/* com.innobiz.orange.web.pt.dao.PtLstSetupMetaDDao.selectPtLstSetupMetaD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.LST_SETUP_META_ID DESC, N.ATRB_ID DESC) AS RNUM, N.* FROM (SELECT
	
	T.LST_SETUP_META_ID,
	T.ATRB_ID,
	T.MSG_ID,
	T.SORT_OPT_VA,
	T.SORT_ORDR,

FROM PT_LST_SETUP_META_D T

	AND T.LST_SETUP_META_ID = #{lstSetupMetaId, jdbcType=VARCHAR}
	AND T.ATRB_ID = #{atrbId, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.LST_SETUP_META_ID, T.ATRB_ID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.pt.dao.PtLstSetupMetaDDao.countPtLstSetupMetaD */
SELECT COUNT(*) CNT
FROM PT_LST_SETUP_META_D T

	AND T.LST_SETUP_META_ID = #{lstSetupMetaId, jdbcType=VARCHAR}
	AND T.ATRB_ID = #{atrbId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtLstSetupMetaDDao.insertPtLstSetupMetaD */ INSERT INTO PT_LST_SETUP_META_D
LST_SETUP_META_ID, ATRB_ID, MSG_ID, SORT_OPT_VA, SORT_ORDR,
#{lstSetupMetaId, jdbcType=VARCHAR}, #{atrbId, jdbcType=VARCHAR}, #{msgId, jdbcType=VARCHAR}, #{sortOptVa, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SORT_ORDR),0)+1 FROM PT_LST_SETUP_META_D WHERE LST_SETUP_META_ID = #{lstSetupMetaId, jdbcType=VARCHAR} AND ATRB_ID = #{atrbId, jdbcType=VARCHAR}),

mapper.update:
/* com.innobiz.orange.web.pt.dao.PtLstSetupMetaDDao.updatePtLstSetupMetaD */
UPDATE PT_LST_SETUP_META_D

	LST_SETUP_META_ID = #{lstSetupMetaId, jdbcType=VARCHAR},
