	MSG_ID = #{msgId, jdbcType=VARCHAR},
	SORT_OPT_VA = #{sortOptVa, jdbcType=VARCHAR},
	SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},


	AND LST_SETUP_META_ID = #{lstSetupMetaId, jdbcType=VARCHAR}
	AND ATRB_ID = #{atrbId, jdbcType=VARCHAR}
	${whereSqllet}

