/* com.innobiz.orange.web.pt.dao.PtLstSetupMetaDDao.deletePtLstSetupMetaD */
DELETE FROM PT_LST_SETUP_META_D

	AND LST_SETUP_META_ID = #{lstSetupMetaId, jdbcType=VARCHAR}
