mapper.select:
/* com.innobiz.orange.web.pt.dao.PtMnuGrpBDao.countPtMnuGrpB */
SELECT COUNT(*) CNT
FROM PT_MNU_GRP_B T, PT_RESC_B R

	AND R.LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko') AND R.RESC_ID = T.RESC_ID AND R.RESC_VA LIKE '%'+#{schWord, jdbcType=VARCHAR}+'%'
	AND T.MNU_GRP_ID = #{mnuGrpId, jdbcType=VARCHAR}
	AND T.MNU_GRP_MD_CD = #{mnuGrpMdCd, jdbcType=VARCHAR}
	AND T.SYSG_YN = #{sysgYn, jdbcType=VARCHAR}
	AND T.OPEN_COMP_ID = #{openCompId, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	AND T.OPEN_COMP_ID IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtMnuGrpBDao.insertPtMnuGrpB */ INSERT INTO PT_MNU_GRP_B
MNU_GRP_ID, MNU_GRP_MD_CD, MNU_GRP_NM, RESC_ID, SYSG_YN, MNU_GRP_TYP_CD, SORT_ORDR, MNU_URL, POP_SETUP_CONT, PLT_LOUT_CD, OPEN_COMP_ID, MD_RID, USE_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{mnuGrpId, jdbcType=VARCHAR}, #{mnuGrpMdCd, jdbcType=VARCHAR}, #{mnuGrpNm, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{sysgYn, jdbcType=VARCHAR}, #{mnuGrpTypCd, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SORT_ORDR),0)+1 FROM PT_MNU_GRP_B WHERE MNU_GRP_MD_CD = #{mnuGrpMdCd, jdbcType=VARCHAR} AND SYSG_YN = #{sysgYn, jdbcType=VARCHAR}), #{mnuUrl, jdbcType=NVARCHAR}, #{popSetupCont, jdbcType=VARCHAR}, #{pltLoutCd, jdbcType=VARCHAR}, #{openCompId, jdbcType=VARCHAR}, #{mdRid, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, GETDATE(),GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.pt.dao.PtMnuGrpBDao.updatePtMnuGrpB */ UPDATE PT_MNU_GRP_B
MNU_GRP_ID = #{mnuGrpId, jdbcType=VARCHAR}, MNU_GRP_NM = #{mnuGrpNm, jdbcType=NVARCHAR}, RESC_ID = #{rescId, jdbcType=VARCHAR}, SYSG_YN = #{sysgYn, jdbcType=VARCHAR}, MNU_GRP_TYP_CD = #{mnuGrpTypCd, jdbcType=VARCHAR}, SORT_ORDR = #{sortOrdr, jdbcType=INTEGER}, MNU_URL = #{mnuUrl, jdbcType=NVARCHAR}, POP_SETUP_CONT = #{popSetupCont, jdbcType=VARCHAR}, PLT_LOUT_CD = #{pltLoutCd, jdbcType=VARCHAR}, OPEN_COMP_ID = #{openCompId, jdbcType=VARCHAR}, MD_RID = #{mdRid, jdbcType=VARCHAR}, USE_YN = #{useYn, jdbcType=VARCHAR}, REGR_UID = #{regrUid, jdbcType=VARCHAR},
REG_DT = GETDATE(), REG_DT = NULL, REG_DT = CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),
MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = GETDATE(), MOD_DT = NULL, MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
AND MNU_GRP_ID = #{mnuGrpId, jdbcType=VARCHAR} AND MNU_GRP_MD_CD = #{mnuGrpMdCd, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtMnuGrpBDao.deletePtMnuGrpB */
DELETE FROM PT_MNU_GRP_B

	AND MNU_GRP_ID = #{mnuGrpId, jdbcType=VARCHAR}
	AND MNU_GRP_MD_CD = #{mnuGrpMdCd, jdbcType=VARCHAR}
	${whereSqllet}
