mapper.select:
/* com.innobiz.orange.web.pt.dao.PtPltDDao.selectPtPltD */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.PLT_ID DESC) AS RNUM, N.* FROM (SELECT
T.PLT_ID, T.PLT_CAT_CD, T.OPEN_COMP_ID, T.PLT_NM, T.RESC_ID, T.PLT_URL, T.MORE_URL, T.SETUP_URL, T.WDTH_PX, T.HGHT_PX, T.PLT_DESC, T.PLT_TITL_YN, T.ITEM_TITL_YN, T.BORD_YN, T.USE_YN, T.REGR_UID, CONVERT(VARCHAR(19), T.REG_DT, 120) REG_DT, T.MODR_UID, CONVERT(VARCHAR(19), T.MOD_DT, 120) MOD_DT, (SELECT RESC_VA FROM PT_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM PT_CD_B WHERE CLS_CD = 'PLT_CAT_CD' AND CD = T.PLT_CAT_CD) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) PLT_CAT_NM, (SELECT RESC_VA FROM PT_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) RESC_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) REGR_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.MODR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) MODR_NM,
FROM PT_PLT_D T, PT_RESC_B R
AND R.LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko') AND R.RESC_ID = T.RESC_ID AND R.RESC_VA LIKE '%'+#{schWord, jdbcType=VARCHAR}+'%' AND T.PLT_ID = #{pltId, jdbcType=VARCHAR} AND T.PLT_CAT_CD = #{pltCatCd, jdbcType=VARCHAR} AND T.OPEN_COMP_ID = #{openCompId, jdbcType=VARCHAR} AND T.USE_YN = #{useYn, jdbcType=VARCHAR} AND T.OPEN_COMP_ID IN ( #{item, jdbcType=VARCHAR} ) ${whereSqllet}
ORDER BY ${orderBy} ORDER BY T.PLT_ID
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.pt.dao.PtPltDDao.countPtPltD */
SELECT COUNT(*) CNT
FROM PT_PLT_D T, PT_RESC_B R

	AND R.LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko') AND R.RESC_ID = T.RESC_ID AND R.RESC_VA LIKE '%'+#{schWord, jdbcType=VARCHAR}+'%'
	AND T.PLT_ID = #{pltId, jdbcType=VARCHAR}
	AND T.PLT_CAT_CD = #{pltCatCd, jdbcType=VARCHAR}
	AND T.OPEN_COMP_ID = #{openCompId, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	AND T.OPEN_COMP_ID IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtPltDDao.insertPtPltD */ INSERT INTO PT_PLT_D
PLT_ID, PLT_CAT_CD, OPEN_COMP_ID, PLT_NM, RESC_ID, PLT_URL, MORE_URL, SETUP_URL, WDTH_PX, HGHT_PX, PLT_DESC, PLT_TITL_YN, ITEM_TITL_YN, BORD_YN, USE_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{pltId, jdbcType=VARCHAR}, #{pltCatCd, jdbcType=VARCHAR}, #{openCompId, jdbcType=VARCHAR}, #{pltNm, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{pltUrl, jdbcType=NVARCHAR}, #{moreUrl, jdbcType=NVARCHAR}, #{setupUrl, jdbcType=NVARCHAR}, NULL#{wdthPx, jdbcType=INTEGER}, NULL#{hghtPx, jdbcType=INTEGER}, #{pltDesc, jdbcType=NVARCHAR}, #{pltTitlYn, jdbcType=VARCHAR}, #{itemTitlYn, jdbcType=VARCHAR}, #{bordYn, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, GETDATE(),GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.pt.dao.PtPltDDao.updatePtPltD */ UPDATE PT_PLT_D
PLT_ID = #{pltId, jdbcType=VARCHAR}, PLT_CAT_CD = #{pltCatCd, jdbcType=VARCHAR}, OPEN_COMP_ID = #{openCompId, jdbcType=VARCHAR}, PLT_NM = #{pltNm, jdbcType=NVARCHAR}, RESC_ID = #{rescId, jdbcType=VARCHAR}, PLT_URL = #{pltUrl, jdbcType=NVARCHAR}, MORE_URL = #{moreUrl, jdbcType=NVARCHAR}, SETUP_URL = #{setupUrl, jdbcType=NVARCHAR}, WDTH_PX = NULL#{wdthPx, jdbcType=INTEGER}, HGHT_PX = NULL#{hghtPx, jdbcType=INTEGER}, PLT_DESC = #{pltDesc, jdbcType=NVARCHAR}, PLT_TITL_YN = #{pltTitlYn, jdbcType=VARCHAR}, ITEM_TITL_YN = #{itemTitlYn, jdbcType=VARCHAR}, BORD_YN = #{bordYn, jdbcType=VARCHAR}, USE_YN = #{useYn, jdbcType=VARCHAR}, REGR_UID = #{regrUid, jdbcType=VARCHAR},
REG_DT = GETDATE(), REG_DT = NULL, REG_DT = CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),
MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = GETDATE(), MOD_DT = NULL, MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
AND PLT_ID = #{pltId, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtPltDDao.deletePtPltD */
DELETE FROM PT_PLT_D

	AND PLT_ID = #{pltId, jdbcType=VARCHAR}
	${whereSqllet}
