mapper.select:
/* com.innobiz.orange.web.pt.dao.PtPltSetupRDao.countPtPltSetupR */
SELECT COUNT(*) CNT
FROM PT_PLT_SETUP_R T

	AND T.MNU_GRP_ID = #{mnuGrpId, jdbcType=VARCHAR}
	AND T.PLT_ID = #{pltId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtPltSetupRDao.insertPtPltSetupR */ INSERT INTO PT_PLT_SETUP_R
MNU_GRP_ID, PLT_ID, PLT_ZONE_CD, RESC_ID, SORT_ORDR, TOP_PX, LEFT_PX, WDTH_PX, HGHT_PX, ZIDX,
#{mnuGrpId, jdbcType=VARCHAR}, #{pltId, jdbcType=VARCHAR}, #{pltZoneCd, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SORT_ORDR),0)+1 FROM PT_PLT_SETUP_R), NULL#{topPx, jdbcType=INTEGER}, NULL#{leftPx, jdbcType=INTEGER}, NULL#{wdthPx, jdbcType=INTEGER}, NULL#{hghtPx, jdbcType=INTEGER}, NULL#{zidx, jdbcType=INTEGER},

mapper.update:
/* com.innobiz.orange.web.pt.dao.PtPltSetupRDao.updatePtPltSetupR */ UPDATE PT_PLT_SETUP_R
MNU_GRP_ID = #{mnuGrpId, jdbcType=VARCHAR}, PLT_ID = #{pltId, jdbcType=VARCHAR}, PLT_ZONE_CD = #{pltZoneCd, jdbcType=VARCHAR}, RESC_ID = #{rescId, jdbcType=VARCHAR}, SORT_ORDR = NULL#{sortOrdr, jdbcType=INTEGER}, TOP_PX = NULL#{topPx, jdbcType=INTEGER}, LEFT_PX = NULL#{leftPx, jdbcType=INTEGER}, WDTH_PX = NULL#{wdthPx, jdbcType=INTEGER}, HGHT_PX = NULL#{hghtPx, jdbcType=INTEGER}, ZIDX = NULL#{zidx, jdbcType=INTEGER},
AND MNU_GRP_ID = #{mnuGrpId, jdbcType=VARCHAR} AND PLT_ID = #{pltId, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtPltSetupRDao.deletePtPltSetupR */
DELETE FROM PT_PLT_SETUP_R

	AND MNU_GRP_ID = #{mnuGrpId, jdbcType=VARCHAR}
