mapper.select:
/* com.innobiz.orange.web.pt.dao.PtRescBDao.selectPtRescB */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.RESC_ID DESC, N.LANG_TYP_CD DESC) AS RNUM, N.* FROM (SELECT
	
	T.RESC_ID,
	T.LANG_TYP_CD,
	T.RESC_VA,

FROM PT_RESC_B T

	AND T.RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	AND T.RESC_ID IN (SELECT RESC_ID FROM PT_MNU_D WHERE MNU_PID = #{mnuPid, jdbcType=VARCHAR})
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.RESC_ID, T.LANG_TYP_CD


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.pt.dao.PtRescBDao.countPtRescB */
SELECT COUNT(*) CNT
FROM PT_RESC_B T

	AND T.RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	AND T.RESC_ID IN (SELECT RESC_ID FROM PT_MNU_D WHERE MNU_PID = #{mnuPid, jdbcType=VARCHAR})
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtRescBDao.insertPtRescB */ INSERT INTO PT_RESC_B
RESC_ID, LANG_TYP_CD, RESC_VA,
#{rescId, jdbcType=VARCHAR}, #{langTypCd, jdbcType=VARCHAR}, #{rescVa, jdbcType=NVARCHAR},

mapper.update:
/* com.innobiz.orange.web.pt.dao.PtRescBDao.updatePtRescB */
UPDATE PT_RESC_B

	RESC_ID = #{rescId, jdbcType=VARCHAR},
	LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR},
	RESC_VA = #{rescVa, jdbcType=NVARCHAR},


	AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtRescBDao.deletePtRescB */
DELETE FROM PT_RESC_B

	AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	AND RESC_ID IN (SELECT RESC_ID FROM PT_CD_B WHERE CLS_CD = #{clsCd, jdbcType=VARCHAR})
	${whereSqllet}
