mapper.select:
/* com.innobiz.orange.web.pt.dao.PtRescDDao.selectPtRescD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.COMP_ID DESC, N.RESC_ID DESC, N.LANG_TYP_CD DESC) AS RNUM, N.* FROM (SELECT
	
	T.COMP_ID,
	T.RESC_ID,
	T.LANG_TYP_CD,
	T.RESC_VA,

FROM PT_RESC_D T

	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.COMP_ID, T.RESC_ID, T.LANG_TYP_CD


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.pt.dao.PtRescDDao.countPtRescD */
SELECT COUNT(*) CNT
FROM PT_RESC_D T

	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtRescDDao.insertPtRescD */ INSERT INTO PT_RESC_D
COMP_ID, RESC_ID, LANG_TYP_CD, RESC_VA,
#{compId, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{langTypCd, jdbcType=VARCHAR}, #{rescVa, jdbcType=NVARCHAR},

mapper.update:
/* com.innobiz.orange.web.pt.dao.PtRescDDao.updatePtRescD */
UPDATE PT_RESC_D

	COMP_ID = #{compId, jdbcType=VARCHAR},
	RESC_ID = #{rescId, jdbcType=VARCHAR},
	LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR},
	RESC_VA = #{rescVa, jdbcType=NVARCHAR},


	AND COMP_ID = #{compId, jdbcType=VARCHAR}
	AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtRescDDao.deletePtRescD */
DELETE FROM PT_RESC_D

	AND COMP_ID = #{compId, jdbcType=VARCHAR}
	AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	${whereSqllet}
