mapper.select:
/* com.innobiz.orange.web.pt.dao.PtUserSetupDDao.selectPtUserSetupD */

SELECT

	M.*,
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = M.USER_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) USER_NM,

FROM ( SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.USER_UID DESC, N.SETUP_CLS_ID DESC, N.SETUP_ID DESC) AS RNUM, N.* FROM (SELECT
	
	T.USER_UID,
	T.SETUP_CLS_ID,
	T.SETUP_ID,
	T.SETUP_VA,
	T.CACHE_YN,
	
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.USER_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) USER_NM,
	

FROM PT_USER_SETUP_D T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.SETUP_CLS_ID = #{setupClsId, jdbcType=VARCHAR}
	AND T.SETUP_ID = #{setupId, jdbcType=VARCHAR}
	AND T.CACHE_YN = #{cacheYn, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.USER_UID, T.SETUP_CLS_ID, T.SETUP_ID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.pt.dao.PtUserSetupDDao.countPtUserSetupD */
SELECT COUNT(*) CNT
FROM PT_USER_SETUP_D T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.SETUP_CLS_ID = #{setupClsId, jdbcType=VARCHAR}
	AND T.SETUP_ID = #{setupId, jdbcType=VARCHAR}
	AND T.CACHE_YN = #{cacheYn, jdbcType=VARCHAR}
	${whereSqllet}

