USER_UID, SETUP_CLS_ID, SETUP_ID, SETUP_VA, CACHE_YN,
#{userUid, jdbcType=VARCHAR}, #{setupClsId, jdbcType=VARCHAR}, #{setupId, jdbcType=VARCHAR}, #{setupVa, jdbcType=NVARCHAR}, #{cacheYn, jdbcType=VARCHAR},

