mapper.select:
/* com.innobiz.orange.web.st.dao.StSiteImgDDao.countStSiteImgD */
SELECT COUNT(*) CNT
FROM ST_SITE_IMG_D T

	AND T.SITE_ID = #{siteId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.st.dao.StSiteImgDDao.insertStSiteImgD */ INSERT INTO ST_SITE_IMG_D
SITE_ID, IMG_PATH, IMG_WDTH, IMG_HGHT, MODR_UID, MOD_DT,
#{siteId, jdbcType=VARCHAR}, #{imgPath, jdbcType=VARCHAR}, #{imgWdth, jdbcType=INTEGER}, #{imgHght, jdbcType=INTEGER}, #{modrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.st.dao.StSiteImgDDao.updateStSiteImgD */ UPDATE ST_SITE_IMG_D
IMG_PATH = #{imgPath, jdbcType=VARCHAR}, IMG_WDTH = #{imgWdth, jdbcType=INTEGER}, IMG_HGHT = #{imgHght, jdbcType=INTEGER}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = GETDATE(), MOD_DT = NULL, MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
AND SITE_ID = #{siteId, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.st.dao.StSiteImgDDao.deleteStSiteImgD */
DELETE FROM ST_SITE_IMG_D

	AND SITE_ID = #{siteId, jdbcType=VARCHAR}
	${whereSqllet}
