mapper.select:
/* com.innobiz.orange.web.wa.dao.WaCarKndLDao.selectWaCarKndL */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.SORT_ORDR ASC) AS RNUM, N.* FROM (SELECT
T.CAR_KND_NO, T.COMP_ID, T.CORP_NO, T.CAR_KND_NM, T.CAR_REG_NO, T.CONT, T.SORT_ORDR,
FROM WA_CAR_KND_L T
ORDER BY ${orderBy} ORDER BY T.SORT_ORDR ASC
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wa.dao.WaCarKndLDao.countWaCarKndL */
SELECT COUNT(*) CNT
FROM WA_CAR_KND_L T

mapper.insert:
/* com.innobiz.orange.web.wa.dao.WaCarKndLDao.insertWaCarKndL */ INSERT INTO WA_CAR_KND_L
CAR_KND_NO, COMP_ID, CORP_NO, CAR_KND_NM, CAR_REG_NO, CONT, SORT_ORDR,
#{carKndNo, jdbcType=INTEGER}, #{compId, jdbcType=VARCHAR}, #{corpNo, jdbcType=INTEGER}, #{carKndNm, jdbcType=NVARCHAR}, #{carRegNo, jdbcType=NVARCHAR}, #{cont, jdbcType=NVARCHAR}, #{sortOrdr, jdbcType=INTEGER},

mapper.update:
/* com.innobiz.orange.web.wa.dao.WaCarKndLDao.updateWaCarKndL */
UPDATE WA_CAR_KND_L

	 CAR_KND_NM = #{carKndNm, jdbcType=NVARCHAR},
	 CAR_REG_NO = #{carRegNo, jdbcType=NVARCHAR},
	 CONT = #{cont, jdbcType=NVARCHAR},
	 SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},


	 AND CAR_KND_NO = #{carKndNo, jdbcType=INTEGER}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
	 AND CORP_NO = #{corpNo, jdbcType=INTEGER}

mapper.delete:
/* com.innobiz.orange.web.wa.dao.WaCarKndLDao.deleteWaCarKndL */
DELETE FROM WA_CAR_KND_L

	 AND CAR_KND_NO = #{carKndNo, jdbcType=INTEGER}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
	 AND CORP_NO = #{corpNo, jdbcType=INTEGER}

mapper.select:
/* com.innobiz.orange.web.wa.dao.WaCarKndLDao.maxWaCarKndL */
SELECT MAX(SORT_ORDR) AS SORT_ORDR
FROM WA_CAR_KND_L

	 AND CORP_NO = #{corpNo, jdbcType=INTEGER}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
