mapper.select:
/* com.innobiz.orange.web.wb.dao.WbBcAgntAdmBDao.selectWbBcAgntAdmB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.REG_DT DESC) AS RNUM, N.* FROM ( SELECT
WBAA_T.REGR_UID, WBAA_T.AGNT_ADM_UID, WBAA_T.AUTH_CD, CONVERT(VARCHAR(10), WBAA_T.REG_DT, 120) AS REG_DT, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) USER_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = T.ORG_RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) ORG_RESC_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = T.DEPT_RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) DEPT_RESC_NM ,
FROM WB_BC_AGNT_ADM_B WBAA_T INNER JOIN OR_USER_B T ON WBAA_T.REGR_UID = T.USER_UID WBAA_T.AGNT_ADM_UID = T.USER_UID
ORDER BY ${orderBy} ORDER BY WBAA_T.REG_DT DESC
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

mapper.insert:
