mapper.select:
/* com.innobiz.orange.web.wb.dao.WbBcClnsCDao.selectWbBcClnsC */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.CLNS_NM ASC) AS RNUM, N.* 
	FROM (
		SELECT
	
	REGR_UID , CLNS_ID , CLNS_NM ,

FROM WB_BC_CLNS_C WBL_T


	ORDER BY ${orderBy}
	ORDER BY WBL_T.CLNS_NM


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wb.dao.WbBcClnsCDao.countWbBcClnsC */
SELECT COUNT(*) CNT
FROM WB_BC_CLNS_C WBL_T

mapper.insert:
/* com.innobiz.orange.web.wb.dao.WbBcClnsCDao.insertWbBcClnsC */ INSERT INTO WB_BC_CLNS_C
REGR_UID , CLNS_ID , CLNS_NM ,
#{regrUid, jdbcType=VARCHAR},#{clnsId, jdbcType=VARCHAR},#{clnsNm, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.wb.dao.WbBcClnsCDao.updateWbBcClnsC */
UPDATE WB_BC_CLNS_C

	 CLNS_NM = #{clnsNm, jdbcType=VARCHAR},


	 AND REGR_UID = #{regrUid, jdbcType=VARCHAR}
	 AND CLNS_ID = #{clnsId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.wb.dao.WbBcClnsCDao.deleteWbBcClnsC */
DELETE FROM WB_BC_CLNS_C

	 AND REGR_UID = #{regrUid, jdbcType=VARCHAR}
	 AND CLNS_ID = #{clnsId, jdbcType=VARCHAR}
