mapper.select:
/* com.innobiz.orange.web.wb.dao.WbMetngClsCdBDao.selectWbMetngClsCdB */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.REG_DT DESC) AS RNUM, N.* 
	FROM (
		SELECT
	
	COMP_ID , CD_VA , RESC_ID , CONVERT(VARCHAR(10), WMCC_T.REG_DT, 120) REG_DT  , 
	(SELECT RESC_VA FROM WM_RESC_B WHERE RESC_ID = WMCC_T.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) RESC_NM,

FROM WB_METNG_CLS_CD_B WMCC_T


	ORDER BY ${orderBy}
	ORDER BY WMCC_T.REG_DT


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wb.dao.WbMetngClsCdBDao.countWbMetngClsCdB */
SELECT COUNT(*) CNT
FROM WB_METNG_CLS_CD_B WMCC_T

mapper.insert:
/* com.innobiz.orange.web.wb.dao.WbMetngClsCdBDao.insertWbMetngClsCdB */ INSERT INTO WB_METNG_CLS_CD_B
COMP_ID , CD_VA , RESC_ID , REGR_UID , REG_DT
#{compId, jdbcType=VARCHAR},#{cdVa, jdbcType=VARCHAR},#{rescId, jdbcType=VARCHAR},#{regrUid, jdbcType=VARCHAR}, GETDATE(),GETDATE(),NULL,

mapper.update:
/* com.innobiz.orange.web.wb.dao.WbMetngClsCdBDao.updateWbMetngClsCdB */
UPDATE WB_METNG_CLS_CD_B

	 CD_VA = #{cdVa, jdbcType=VARCHAR},
	 MODR_UID = #{modrUid, jdbcType=VARCHAR},
	 
		 MOD_DT = GETDATE(),
		 MOD_DT = NULL,
		 MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
	


	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
	 AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	 AND REGR_UID = #{modrUid, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.wb.dao.WbMetngClsCdBDao.deleteWbMetngClsCdB */
DELETE FROM WB_METNG_CLS_CD_B

	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
	 AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	 AND REGR_UID = #{modrUid, jdbcType=VARCHAR}
