mapper.select:
/* com.innobiz.orange.web.wc.dao.WcAnnvDDao.selectWcAnnvD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}T.NAT_CD ASC) AS RNUM,
	
	T.COMP_ID,
	T.SCHDL_ID,
	T.RESC_ID,
	(SELECT RESC_VA FROM WC_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) RESC_NM,
	T.NAT_CD,

FROM WC_ANNV_D T

ORDER BY ${orderBy}
ORDER BY T.NAT_CD ASC

) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wc.dao.WcAnnvDDao.countWcAnnvD */
SELECT COUNT(*) CNT
FROM WC_ANNV_D T

